class DpFont(object):
    """
    依据css样式中得到的字体文件地址下载并加载后自动判断该文件字体是否有变化
    如果没有变化可直接使用get_decode进行解析，
    如果发生变化则会报错，需对字体映射进行修改
    """

    def __init__(self, fontname):
        """
        fontname动态的字体文件
        woff动态的字体文件数据，初始值为字典
        :param fontname:
        """
        self.fontname = fontname
        self.woff = {}
        if self.__get_check_update():
            raise Exception("字体文件有更新，请查看并修改字典映射关系")

    def __get_font_md5(self):
        """
        获取字体文件的md5值
        :return:
        """
        font = TTFont(self.fontname)
        # 参数'cmap' 表示汉字对应的映射 为unicode编码
        unicode = font['cmap'].tables[1].ttFont.getGlyphOrder()[1::]
        unimd5 = {}
        for i in unicode:
            strdata = font['glyf'].glyphs.get(i)
            if strdata:
                # 获取字体文件中字体的数据并进行MD5
                md5data = hashlib.md5(strdata.data).hexdigest()
                unimd5[i] = md5data
        self.woff = unimd5
        fontmd5 = hashlib.md5(str(sorted(unimd5.values())).encode("utf8")).hexdigest()
        return fontmd5

    def __get_check_update(self):
        """
        检查是否需要修改字体字典映射关系
        :return:
        """
        fontmd5 = self.__get_font_md5()
        if fontmd5 == "53ff14cd2a71d3b4c5b04e7bec51790c":
            # 在字体文件md5字典中找到字体文件的MD5值，则不需要修改返回False
            return False
        else:
            # 在字体文件md5字典中未找到字体文件的MD5值，则需要修改返回True
            return True

    def __code_md5(self, codeaft):
        """
        根据字符找到字符对应的md5值，并得到对应的映射字
        :param codeaft:
        :return:
        """
        codemd5 = self.woff.get(codeaft)
        if codemd5:
            return strMd5Dict.get(codemd5)
        return codeaft

    def get_decode(self, code):
        if isinstance(code, str):
            codeaft = code.replace("&#x", "uni")
            return self.__code_md5(codeaft)
        if isinstance(code, list):
            for num, codepro in enumerate(code):
                codeaft = codepro.replace("&#x", "uni")
                code[num] = self.__code_md5(codeaft)
            return code


